function sortear(){var e=document.getElementById("numero"),o=document.getElementById("resultado"),t=document.getElementById("numeroSorteado");e.value<2?alert("Por favor, insira um número maior ou igual a 2."):(e=parseInt(e.value),isNaN(e)?alert("Por favor, insira um número válido."):(e=Math.floor(Math.random()*(e-2+1))+2,t.textContent=e,o.style.display="block"))}